import { Button, Form, Input, message } from "antd";
import { useEffect, useState } from "react";
import { searchApps } from "../utils";

const HomePage = () => {
    const [loading, setLoading] = useState();
    const [data, setData] = useState();

    useEffect(() => {
        handleSearch();
    }, []);

    const handleSearch = async (query) => {
        setLoading(true);
        try {
            const resp = await searchApps(query);
            setData(resp || []);
        } catch (error) {
            message.error(error.message);
        } finally {
            setLoading(false);
        };
    };

    return (
        <>
            <Form  onFinished={handleSearch} layout="inline">
            <Form.Item label="Title" name="title">
                <Input />
            </Form.Item>
            <Form.Item label="Description" name="description">
                <Input />
            </Form.Item>
            <Form.Item>
                <Button loading={loading} type="primary">
                    Search
                </Button>
            </Form.Item>
            </Form>
            <List 
                style={{ marginTop: 20 }}
                loading={loading}
                grid={{
                    gutter: 16,
                    xs: 1,
                    sm: 3,
                    md: 3,
                    lg: 3,
                    xl: 4,
                    xxl: 4,
                }}
                dataSource={data}
                renderItem={( (item) => (
                    <List.Item>
                        <Card >

                        </Card>
                    </List.Item>
                )

                )}
            />
        </>
    );
};

export default HomePage;
